import React, { Component } from 'react';

class TodoList extends Component {
  constructor () {
    super ();
      this.state = {
        userInput: '',
        items: []
      };
  }
  render () {
    return (
      <div>
        <h1> Ma Todo List : </h1>
          <form>
            <input value={this.state.userInput} text="text" placeholder="Renseigner un item"/>
            <button> Ajouter </button>
          </form>
      </div>
    );
  }
}

export default TodoList;
